/* ===== Concept Art (FINAL): padded, no-crop image boxes + text padding ===== */
/* === Concept Art Gallery: Final with Fixes === */

/* Section spacing */
.gallery-section {
    padding: var(--space-lg) 0 var(--space-xl); /* Large padding top, extra-large padding bottom */
}

/* Grid spacing: Controls how gallery items are arranged */
.gallery {
    display: grid; /* Uses CSS Grid layout system for flexible positioning */
    gap: var(--space-xl); /* Space between each art item */
}

/* On tablets and larger screens, increase the gap slightly */
@media (min-width: 768px) {
    .gallery {
        gap: calc(var(--space-xl) + 8px);
    }

    /* Adds 8 extra pixels to the existing gap */
}

/* Card layout: Each individual art piece container */
.art-item {
    display: grid; /* Each art item uses grid layout internally */
    gap: var(--space-lg); /* Space between image and text sections */
    grid-template-columns: 1fr; /* mobile: single column (stacked layout) */
    /* Define named areas for easier positioning */
    grid-template-areas:
    "image"  /* Image on top */
    "meta"; /* Text below */
    align-items: stretch; /* Makes child elements fill their assigned space */
    border-radius: 16px; /* Rounded corners */
    background: var(--white); /* White background color */
    box-shadow: 0 4px 20px rgba(63, 110, 58, 0.08); /* Subtle shadow for depth */
    transition: transform .3s ease, box-shadow .3s ease; /* Smooth animation for hover effects */
    overflow: visible; /* Allows shadows to show outside the container */
}

/* On tablets and larger screens, arrange image and text side by side */
@media (min-width: 768px) {
    .art-item {
        grid-template-columns: 1fr 1fr; /* Two equal columns */
        grid-template-areas: "image meta"; /* Image left, text right */
    }
}

/* Hover effect: Lifts the card slightly when mouse is over it */
.art-item:hover {
    transform: translateY(-4px); /* Moves the card up by 4 pixels */
    box-shadow: 0 8px 32px rgba(63, 110, 58, 0.12); /* Stronger, larger shadow */
}

/* Image container: Controls how artwork images are displayed */
.art-media {
    grid-area: image; /* Places this in the "image" area defined above */
    position: relative; /* Allows absolute positioning of child elements */
    display: grid; /* Uses grid to center the image */
    place-items: center; /* Centers the image both horizontally and vertically */
    padding: 12px; /* Space around the image */
    box-sizing: border-box; /* Includes padding in the total width/height */
    background: var(--sand); /* Light beige background color */
    border-radius: 12px; /* Rounded corners */
    overflow: hidden; /* Clips anything that goes outside the container */
    min-width: 0; /* Allows the container to shrink smaller than its content */
    min-height: 0; /* Allows the container to shrink smaller than its content */
    height: clamp(220px, 36vw, 460px); /* Responsive height: minimum 220px, maximum 460px, scales with screen width */
}

/* Images and SVGs: Controls how the actual artwork appears */
.art-media > img,
.art-media > svg {
    width: 100% !important; /* Forces image to fill container width (!important overrides other styles) */
    height: 100% !important; /* Forces image to fill container height */
    max-width: 100%; /* Prevents image from exceeding container width */
    max-height: 100%; /* Prevents image from exceeding container height */
    object-fit: fill !important; /* Stretches image to fill container (may distort aspect ratio) */
    aspect-ratio: auto; /* Lets the image use its natural proportions when possible */
    display: block; /* Removes default inline spacing around images */
    -webkit-user-drag: none; /* Prevents users from dragging the image */
    overflow: hidden; /* Clips any parts that extend beyond the container */
}

/* Text panel: Contains the artwork title, description, and details */
.art-meta {
    grid-area: meta; /* Places this in the "meta" area defined above */
    background: var(--sand); /* Light beige background to match image container */
    border-radius: 12px; /* Rounded corners */
    padding: 16px; /* Space inside the container (mobile size) */
    box-sizing: border-box; /* Includes padding in total width/height */
    min-width: 0; /* Allows container to shrink */
    min-height: 0; /* Allows container to shrink */
}

/* On tablets and larger screens, increase the padding */
@media (min-width: 768px) {
    .art-meta {
        padding: 24px;
    }

    /* More generous spacing on larger screens */
}

.art-meta h3 {
    font-family: var(--font-heading); /* Uses the heading font (Poppins) */
    font-size: var(--text-xl); /* Extra large text size */
    font-weight: 600; /* Semi-bold weight */
    color: var(--leaf-dark); /* Dark green color */
    margin-bottom: var(--space-sm); /* Small space below the title */
    line-height: 1.3; /* Tighter line spacing for headings */
}

.art-meta p {
    font-size: var(--text-base); /* Normal text size */
    line-height: 1.6; /* Comfortable reading line spacing */
    margin-bottom: var(--space-md); /* Medium space below description */
    color: var(--leaf-dark); /* Dark green color */
    opacity: 0.9; /* Slightly transparent to make it less prominent than the title */
}

/* Art Facts List: Technical details like year, tools, dimensions */
.art-facts {
    list-style: none; /* Removes bullet points */
    padding: 0; /* Removes default list padding */
    margin: 0; /* Removes default list margin */
    display: flex; /* Uses flexbox layout */
    flex-direction: column; /* Stacks items vertically */
    gap: var(--space-xs); /* Small space between each fact */
}

/* Individual fact items */
.art-facts li {
    font-size: var(--text-sm); /* Small text size */
    color: var(--leaf-dark); /* Dark green color */
    font-weight: 500; /* Medium weight for emphasis */
    padding: var(--space-xs) 0; /* Small vertical padding */
    border-bottom: 1px solid rgba(63, 110, 58, 0.15); /* Thin green separator line */
}

/* Remove border from the last item */
.art-facts li:last-child {
    border-bottom: none;
}

/* Tablet layout: Arrange facts horizontally instead of vertically */
@media (min-width: 480px) {
    .art-facts {
        flex-direction: row; /* Arrange items side by side */
        flex-wrap: wrap; /* Allow wrapping to next line if needed */
        gap: var(--space-sm); /* Medium space between items */
    }

    .art-facts li {
        border-bottom: none; /* Remove bottom borders */
        border-right: 1px solid rgba(63, 110, 58, 0.15); /* Add right borders as separators */
        padding-right: var(--space-sm); /* Space before the border */
        margin-right: var(--space-sm); /* Space after the border */
    }

    /* Remove border from the last item */
    .art-facts li:last-child {
        border-right: none;
        margin-right: 0;
        padding-right: 0;
    }
}

/* Very small phone adjustments */
@media (max-width: 359px) {
    .art-media {
        height: clamp(180px, 45vw, 320px);
    }

    /* Smaller height range for tiny screens */
}

/* Accessibility: Respect user's motion preferences */
@media (prefers-reduced-motion: reduce) {
    .art-item {
        transition: none;
    }

    /* Disable animations for users who prefer reduced motion */
    .art-item:hover {
        transform: none; /* No movement on hover */
        box-shadow: 0 4px 20px rgba(63, 110, 58, 0.08); /* Keep original shadow */
    }
}

/* Accessibility: Keyboard navigation focus indicator */
.art-item:focus-within {
    outline: 3px solid var(--berry); /* Bright pink outline when keyboard navigating */
    outline-offset: 4px; /* Space between outline and element */
}

/* ===== IMAGE LIGHTBOX STYLES ===== */
/* These styles control the popup that appears when clicking on images */

/* Hide lightbox when aria-hidden is true */
.lightbox[aria-hidden="true"] {
    display: none;
}

/* Main lightbox container: Covers the entire screen */
.lightbox {
    position: fixed; /* Stays in place even when scrolling */
    inset: 0; /* Covers entire viewport (top: 0, right: 0, bottom: 0, left: 0) */
    z-index: 3000; /* Appears above all other content */
    display: grid; /* Uses grid layout */
    place-items: center; /* Centers the lightbox content */
}

/* Dark overlay behind the lightbox content */
.lightbox__overlay {
    position: absolute; /* Positioned relative to the lightbox container */
    inset: 0; /* Covers the entire lightbox area */
    background: rgba(0, 0, 0, 0.65); /* Semi-transparent black background */
}

/* The actual lightbox content box */
.lightbox__content {
    position: relative; /* Allows absolute positioning of the close button */
    max-width: min(1200px, 95vw); /* Maximum 1200px wide, but never more than 95% of screen width */
    max-height: 90vh; /* Never taller than 90% of screen height */
    background: var(--white); /* White background */
    border-radius: 12px; /* Rounded corners */
    padding: 12px; /* Space around the content */
    box-shadow: 0 20px 60px rgba(0, 0, 0, .3); /* Strong shadow for depth */
    display: grid; /* Uses grid layout internally */
    grid-template-rows: auto auto; /* Two rows: image and caption */
    gap: 8px; /* Small space between image and caption */
}

/* The large image inside the lightbox */
.lightbox__img {
    width: 100%; /* Fill the available width */
    height: 100%; /* Fill the available height */
    max-height: 75vh; /* Never taller than 75% of screen height */
    object-fit: fill; /* Stretch to fill container (matches gallery style) */
    display: block; /* Removes default inline spacing */
    background: var(--sand); /* Light beige background (matches site theme) */
    border-radius: 8px; /* Slightly rounded corners */
}

/* Caption text below the image */
.lightbox__caption {
    margin: 0; /* Remove default paragraph margins */
    font: 500 var(--text-sm)/1.5 var(--font-body); /* Medium weight, small size, 1.5 line height, body font */
    color: var(--leaf-dark); /* Dark green color */
    padding: 4px 2px 0; /* Small padding around text */
    text-align: center; /* Center the text */
}

/* Close button (X) in the top-right corner */
.lightbox__close {
    position: absolute; /* Positioned relative to lightbox__content */
    top: 8px;
    right: 8px; /* 8px from top and right edges */
    width: 40px;
    height: 40px; /* Square button */
    border-radius: 50%; /* Makes it circular */
    border: 2px solid var(--sand); /* Light border */
    background: var(--leaf-dark); /* Dark green background */
    color: #fff; /* White text/icon */
    font-size: 24px; /* Large X symbol */
    line-height: 1; /* Tight line spacing */
