  color: var(--sand);
  text-decoration: none;
  border-radius: 4px;
}

.developer-credit-link:focus-visible {
  outline: 3px solid var(--berry);
  outline-offset: 2px;
}

.developer-credit-icon {
  width: 18px; height: 18px;
  display: block;
}

{
  "contact": {
    "email": "katelyn@katelynlydia.com"
  },
  "social": [
    { "label": "Instagram", "href": "https://instagram.com/katelynlydia" },
    { "label": "ArtStation", "href": "https://artstation.com/katelynlydia" },
    { "label": "YouTube",   "href": "https://youtube.com/katelynlydia" }
  ],
  "credit": {
    "text": "Website by Matthew Derek Rall",
    "href": "https://mattverstappen.github.io/websites-made.html",
    "icon": "/assets/images/icons/website.svg"
  }
}

/**
 * FOOTER LOADER SCRIPT
 * ====================
 * This script makes the footer content data-driven by loading information from JSON.
 * It handles contact info, social links, and developer credit dynamically.
 * 
 * HOW TO EDIT:
 * 1. Update /assets/data/footer.json to change email, social links, or credit info
 * 2. This script will automatically load and display the new content
 * 3. No need to edit HTML files manually - just update the JSON!
 *
 * WHAT IT DOES:
 * - Fetches footer data from /assets/data/footer.json
 * - Populates contact email in .contact-info elements
 * - Creates social media links in .social-links elements  
 * - Adds developer credit with icon at the bottom of footers
 * - Handles errors gracefully if JSON file is missing
 */

// Wait for the page to fully load before running the script
document.addEventListener('DOMContentLoaded', function() {

    /**
     * MAIN FUNCTION: Load and render footer content
     * This is the main function that fetches JSON data and updates the footer
     */
    async function loadFooterContent() {
        try {
            // Fetch the footer data from our JSON file
            // This contains email, social links, and developer credit info
            const response = await fetch('/assets/data/footer.json');

            // Check if the fetch was successful
            if (!response.ok) {
                throw new Error(`Failed to load footer data: ${response.status}`);
            }

            // Convert the response to JSON format we can use
            const footerData = await response.json();

            // Call functions to update each part of the footer
            updateContactInfo(footerData.contact);
            updateSocialLinks(footerData.social);
            addDeveloperCredit(footerData.credit);

        } catch (error) {
            // If something goes wrong, log it to console for debugging
            console.warn('Footer loader error:', error.message);
            console.warn('Footer will display with existing content only');
        }
    }

    /**
     * UPDATE CONTACT INFO
     * Finds .contact-info elements and adds email link
     * @param {Object} contact - Contact data with email property
     */
    function updateContactInfo(contact) {
        // Find all elements with class 'contact-info' on the page
        const contactElements = document.querySelectorAll('.contact-info');

        // If we found contact elements and have email data, update them
        if (contactElements.length > 0 && contact && contact.email) {
            contactElements.forEach(element => {
                // Replace the content with a proper email link
                // This creates: Email: <a href="mailto:email">email</a>
                element.innerHTML = `Email: <a href="mailto:${contact.email}" class="contact-link">${contact.email}</a>`;
            });
        }
    }

    /**
     * UPDATE SOCIAL LINKS
     * Finds .social-links elements and adds social media links
     * @param {Array} socialLinks - Array of social link objects with label and href
     */
    function updateSocialLinks(socialLinks) {
        // Find all elements with class 'social-links' on the page
        const socialElements = document.querySelectorAll('.social-links');

        // If we found social elements and have social data, update them
        if (socialElements.length > 0 && socialLinks && socialLinks.length > 0) {
            socialElements.forEach(element => {
                // Clear existing content
                element.innerHTML = '';

                // Create a link for each social platform
                socialLinks.forEach(social => {
                    // Create the link element
                    const link = document.createElement('a');
                    link.href = social.href;
                    link.textContent = social.label;
                    link.className = 'social-link';
                    link.target = '_blank'; // Open in new tab
                    link.rel = 'noopener noreferrer'; // Security best practice for external links

                    // Add accessibility label for screen readers
                    link.setAttribute('aria-label', `Visit ${social.label} (opens in new tab)`);

                    // Add the link to the container
                    element.appendChild(link);
                });
            });
        }
    }

    /**
     * ADD DEVELOPER CREDIT
     * Creates or updates developer credit section with link and icon
     * @param {Object} credit - Credit data with text, href, and icon properties
     */
    function addDeveloperCredit(credit) {
        // If no credit data provided, skip this step
        if (!credit || !credit.text || !credit.href) {
            return;
        }

        // Find all footer elements on the page
        const footers = document.querySelectorAll('footer');

        footers.forEach(footer => {
            // Look for existing developer credit to avoid duplicates
            let existingCredit = footer.querySelector('.developer-credit');

            if (existingCredit) {
                // If credit already exists, remove it so we can add the updated version
                existingCredit.remove();
            }

            // Create the developer credit container
            const creditElement = document.createElement('p');
            creditElement.className = 'developer-credit';

            // Create the credit text span
            const textSpan = document.createElement('span');
            textSpan.className = 'developer-credit-text';
            textSpan.textContent = credit.text;

            // Create the credit link with icon
            const creditLink = document.createElement('a');
            creditLink.href = credit.href;
            creditLink.className = 'developer-credit-link';
            creditLink.target = '_blank'; // Open in new tab
            creditLink.rel = 'noopener noreferrer'; // Security best practice
            creditLink.setAttribute('aria-label', `${credit.text} (opens in new tab)`);

            // Create the icon image
            const iconImg = document.createElement('img');
            iconImg.src = credit.icon;
            iconImg.alt = ''; // Empty alt since the link has aria-label
            iconImg.className = 'developer-credit-icon';

            // Put it all together: text + link with icon
            creditLink.appendChild(iconImg);
            creditElement.appendChild(textSpan);
            creditElement.appendChild(creditLink);

            // Find the best place to insert the credit
            const container = footer.querySelector('.container');
            const copyright = footer.querySelector('.copyright');

            if (container) {
                if (copyright) {
                    // Insert after copyright if it exists
                    copyright.parentNode.insertBefore(creditElement, copyright.nextSibling);
                } else {
                    // Otherwise add at the end of the container
                    container.appendChild(creditElement);
                }
            }
        });
    }

    // Start the footer loading process
    loadFooterContent();
});

/*
HEADER NAVIGATION FUNCTIONALITY:
- Mobile hamburger menu toggle
- Auto-hide header on scroll down, show on scroll up
- Accessibility support with ARIA attributes
- Close menu when clicking nav links (mobile)
- Keyboard navigation support
*/

(function() {
  'use strict';

  let lastScrollY = 0;
  let ticking = false;

  // Initialize when DOM is ready
  document.addEventListener('DOMContentLoaded', function() {
    initMobileNav();
    initScrollHeader();
   * Initialize scroll-to-hide header functionality
   */
  function initScrollHeader() {
    const header = document.querySelector('.site-header');
    if (!header) return;

    function updateHeader() {
      const currentScrollY = window.scrollY;

      if (currentScrollY < 10) {
        // Always show header at top of page
        header.classList.remove('header-hidden');
      } else if (currentScrollY > lastScrollY && currentScrollY > 100) {
        // Scrolling down - hide header
        header.classList.add('header-hidden');
      } else if (currentScrollY < lastScrollY) {
        // Scrolling up - show header
        header.classList.remove('header-hidden');
      }

      lastScrollY = currentScrollY;
      ticking = false;
    }

    function requestTick() {
      if (!ticking) {
        requestAnimationFrame(updateHeader);
        ticking = true;
      }
    }

    window.addEventListener('scroll', requestTick, { passive: true });
  }

  /**
   * Initialize mobile navigation functionality
   */
  function initMobileNav() {
    const navToggle = document.querySelector('.nav-toggle');
    const mainNav = document.querySelector('.main-nav');
    const navLinks = document.querySelectorAll('.nav-link');

    if (!navToggle || !mainNav) return;

    // Toggle mobile navigation
    navToggle.addEventListener('click', function() {
      const isExpanded = navToggle.getAttribute('aria-expanded') === 'true';
      const newState = !isExpanded;

      // Update ARIA attributes
      navToggle.setAttribute('aria-expanded', newState);

      // Toggle nav visibility
      mainNav.classList.toggle('nav-open', newState);
      navToggle.classList.toggle('nav-toggle-active', newState);

      // Focus management
      if (newState) {
        const firstNavLink = mainNav.querySelector('.nav-link');
        if (firstNavLink) {
          setTimeout(() => firstNavLink.focus(), 100);
        }
      }
    });

    // Close mobile nav when clicking nav links
    navLinks.forEach(function(link) {
      link.addEventListener('click', function() {
        if (window.innerWidth < 768) {
          closeMobileNav();
        }
      });
    });

    // Close mobile nav on escape key
    document.addEventListener('keydown', function(e) {
      if (e.code === 'Escape' && navToggle.getAttribute('aria-expanded') === 'true') {
        closeMobileNav();
        navToggle.focus();
      }
    });

    // Close mobile nav on window resize to desktop
    window.addEventListener('resize', function() {
      if (window.innerWidth >= 768) {
        closeMobileNav();
      }
    });

    /**
     * Close mobile navigation
     */
    function closeMobileNav() {
      navToggle.setAttribute('aria-expanded', 'false');
      mainNav.classList.remove('nav-open');
      navToggle.classList.remove('nav-toggle-active');
    }
  }

})();

/*
HEADER NAVIGATION FUNCTIONALITY:
- Mobile hamburger menu toggle
- Auto-hide header on scroll down, show on scroll up
- Accessibility support with ARIA attributes
- Close menu when clicking nav links (mobile)
- Keyboard navigation support
*/

(function() {
  'use strict';

  let lastScrollY = 0;
  let ticking = false;

  // Initialize when DOM is ready
  document.addEventListener('DOMContentLoaded', function() {
    initMobileNav();
    initScrollHeader();
    initSlideMenu();
  });

  /**
   * Initialize scroll-to-hide header functionality
   */
  function initScrollHeader() {
    const header = document.querySelector('.site-header');
    if (!header) return;

    function updateHeader() {
      const currentScrollY = window.scrollY;

      if (currentScrollY < 10) {
        // Always show header at top of page
        header.classList.remove('header-hidden');
      } else if (currentScrollY > lastScrollY && currentScrollY > 100) {
        // Scrolling down - hide header
        header.classList.add('header-hidden');
      } else if (currentScrollY < lastScrollY) {
        // Scrolling up - show header
        header.classList.remove('header-hidden');
      }

      lastScrollY = currentScrollY;
      ticking = false;
    }

    function requestTick() {
      if (!ticking) {
        requestAnimationFrame(updateHeader);
        ticking = true;
      }
    }

    window.addEventListener('scroll', requestTick, { passive: true });
  }

  /**
   * Initialize mobile navigation functionality
   */
  function initMobileNav() {
    const navToggle = document.querySelector('.nav-toggle');
    const mainNav = document.querySelector('.main-nav');
    const navLinks = document.querySelectorAll('.nav-link');

    if (!navToggle || !mainNav) return;

    // Toggle mobile navigation
    navToggle.addEventListener('click', function() {
      const isExpanded = navToggle.getAttribute('aria-expanded') === 'true';
      const newState = !isExpanded;

      // Update ARIA attributes
      navToggle.setAttribute('aria-expanded', newState);

      // Toggle nav visibility
      mainNav.classList.toggle('nav-open', newState);
      navToggle.classList.toggle('nav-toggle-active', newState);

      // Focus management
      if (newState) {
        const firstNavLink = mainNav.querySelector('.nav-link');
        if (firstNavLink) {
          setTimeout(() => firstNavLink.focus(), 100);
        }
      }
    });

    // Close mobile nav when clicking nav links
    navLinks.forEach(function(link) {
      link.addEventListener('click', function() {
        if (window.innerWidth < 768) {
          closeMobileNav();
        }
      });
    });

    // Close mobile nav on escape key
    document.addEventListener('keydown', function(e) {
      if (e.code === 'Escape' && navToggle.getAttribute('aria-expanded') === 'true') {
        closeMobileNav();
        navToggle.focus();
      }
    });

    // Close mobile nav on window resize to desktop
    window.addEventListener('resize', function() {
      if (window.innerWidth >= 768) {
        closeMobileNav();
      }
    });

    /**
     * Close mobile navigation
     */
    function closeMobileNav() {
      navToggle.setAttribute('aria-expanded', 'false');
      mainNav.classList.remove('nav-open');
      navToggle.classList.remove('nav-toggle-active');
    }
  }

  /*
  SLIDE-OUT MENU FUNCTIONALITY:
  - Clean header with menu button that opens slide-out panel
  - Menu slides in from the right side
  - Header stays visible during scroll (no auto-hide)
  - Accessibility support with ARIA attributes and keyboard navigation
  */

  /**
   * Initialize slide-out menu functionality
   */
  function initSlideMenu() {
    const menuToggle = document.querySelector('.menu-toggle');
    const slideMenu = document.querySelector('.slide-menu');
    const menuClose = document.querySelector('.menu-close');
    const menuOverlay = document.querySelector('.slide-menu-overlay');
    const menuLinks = document.querySelectorAll('.menu-link');

    if (!menuToggle || !slideMenu) return;

    // Open menu
    menuToggle.addEventListener('click', function() {
      openMenu();
    });

    // Close menu - close button
    if (menuClose) {
      menuClose.addEventListener('click', function() {
        closeMenu();
      });
    }

    // Close menu - overlay click
    if (menuOverlay) {
      menuOverlay.addEventListener('click', function() {
        closeMenu();
      });
    }

    // Close menu - escape key
    document.addEventListener('keydown', function(e) {
      if (e.code === 'Escape' && slideMenu.classList.contains('menu-open')) {
        closeMenu();
        menuToggle.focus();
      }
    });

    // Close menu when clicking menu links
    menuLinks.forEach(function(link) {
      link.addEventListener('click', function() {
        closeMenu();
      });
    });

    // Prevent body scroll when menu is open
    function openMenu() {
      slideMenu.classList.add('menu-open');
      slideMenu.setAttribute('aria-hidden', 'false');
      menuToggle.setAttribute('aria-expanded', 'true');
      document.body.style.overflow = 'hidden';

      // Focus first menu link for keyboard users
      const firstMenuLink = slideMenu.querySelector('.menu-link');
      if (firstMenuLink) {
        setTimeout(() => firstMenuLink.focus(), 300);
      }
    }

    function closeMenu() {
      slideMenu.classList.remove('menu-open');
      slideMenu.setAttribute('aria-hidden', 'true');
      menuToggle.setAttribute('aria-expanded', 'false');
      document.body.style.overflow = '';
    }

    // Handle focus trapping in menu
    slideMenu.addEventListener('keydown', function(e) {
      if (e.code === 'Tab' && slideMenu.classList.contains('menu-open')) {
        const focusableElements = slideMenu.querySelectorAll('button, a');
        const firstElement = focusableElements[0];
        const lastElement = focusableElements[focusableElements.length - 1];

        if (e.shiftKey && document.activeElement === firstElement) {
          e.preventDefault();
          lastElement.focus();
        } else if (!e.shiftKey && document.activeElement === lastElement) {
          e.preventDefault();
          firstElement.focus();
        }
      }
    });
  }

})();

// LIGHTBOX FUNCTIONALITY
// ======================
// This JavaScript file creates a popup (lightbox) that displays larger versions of images
// when users click on the small thumbnail images in the gallery.

// IIFE (Immediately Invoked Function Expression) - this runs the code right away
// and keeps all variables private so they don't interfere with other scripts
(function () {
  // Find the lightbox element in the HTML (the popup container)
  const lb = document.getElementById('lightbox');
  // If there's no lightbox element on the page, stop running this script
  if (!lb) return;

  // Find the different parts of the lightbox that we'll need to control
  const imgEl = lb.querySelector('.lightbox__img'); // The large image that will be displayed
  const captionEl = lb.querySelector('.lightbox__caption'); // The text description under the image
  const overlayEls = lb.querySelectorAll('[data-lightbox-close]'); // All elements that close the lightbox when clicked
  // Remember which element was focused before opening the lightbox (for accessibility)
  let lastActive = null;

  // FUNCTION: Open the lightbox with a specific image
  // This runs when someone clicks on a thumbnail image
  function openLightbox(fromImg) {
    // Remember what was focused before (so we can return focus when closing)
    lastActive = document.activeElement;

    // Get the image source - try data-full attribute first, then currentSrc, then regular src
    // data-full allows us to show a higher quality image in the lightbox than the thumbnail
    const full = fromImg.getAttribute('data-full') || fromImg.currentSrc || fromImg.src;
    // Get the alt text (description) from the clicked image
    const alt = fromImg.getAttribute('alt') || '';

    // Set the large image source and description
    imgEl.src = full;
    imgEl.alt = alt;
    captionEl.textContent = alt; // Show the description below the image

    // Make the lightbox visible by changing its aria-hidden attribute
    lb.setAttribute('aria-hidden', 'false');
    // Prevent the background page from scrolling while lightbox is open
    document.documentElement.classList.add('no-scroll');

    // Put keyboard focus on the close button (good for accessibility)
    lb.querySelector('.lightbox__close').focus();

    // Start listening for keyboard presses (like Escape key to close)
    document.addEventListener('keydown', onKeydown);
  }

  // FUNCTION: Close the lightbox and clean up
  function closeLightbox() {
    // Hide the lightbox
    lb.setAttribute('aria-hidden', 'true');

    // Clear the image and caption to free up memory
    imgEl.removeAttribute('src');
    imgEl.removeAttribute('alt');
    captionEl.textContent = '';

    // Allow the background page to scroll again
    document.documentElement.classList.remove('no-scroll');

    // Stop listening for keyboard presses
    document.removeEventListener('keydown', onKeydown);

    // Return keyboard focus to whatever was focused before opening the lightbox
    if (lastActive && typeof lastActive.focus === 'function') {
      lastActive.focus();
    }
  }

  // FUNCTION: Handle keyboard presses when lightbox is open
  function onKeydown(e) {
    // If user presses Escape key, close the lightbox
    if (e.key === 'Escape') { 
      e.preventDefault(); // Stop the default action
      closeLightbox(); 
      return; 
    }

    // Handle Tab key for keyboard navigation (accessibility feature)
    // This creates a "focus trap" - Tab only moves between elements inside the lightbox
    if (e.key === 'Tab') {
      // Define which elements can receive focus inside the lightbox
      const focusables = [lb.querySelector('.lightbox__close'), imgEl];
      // Find which element currently has focus
      const idx = focusables.indexOf(document.activeElement);

      // If Shift+Tab (reverse direction)
      if (e.shiftTab) {
        e.preventDefault();
        // Move to previous focusable element (wrapping around if needed)
        focusables[(idx - 1 + focusables.length) % focusables.length].focus();
      } else {
        e.preventDefault();
        // Move to next focusable element (wrapping around if needed)
        focusables[(idx + 1) % focusables.length].focus();
      }
    }
  }

  // EVENT LISTENER: Listen for clicks anywhere on the page
  // This uses "event delegation" - one listener handles clicks for all images
  document.addEventListener('click', function (e) {
    // Check if the clicked element is a thumbnail image inside an art-media container
    const img = e.target.closest('.art-media .art-thumb');
    if (img) {
      e.preventDefault(); // Prevent any default link behavior
      openLightbox(img); // Open the lightbox with this image
    }

    // Check if the clicked element has the data-lightbox-close attribute
    // This includes the overlay background and close button
    if (e.target.closest('[data-lightbox-close]')) {
      e.preventDefault(); // Prevent any default behavior
      closeLightbox(); // Close the lightbox
    }
  });

  // EVENT LISTENER: Close lightbox when clicking on overlay elements
  // This is a backup in case the click delegation above doesn't work
  overlayEls.forEach(el => el.addEventListener('click', closeLightbox));
})(); // End of IIFE - the parentheses here make the function run immediately

/**
 * PROJECTS LOADER
 * ===============
 * This JavaScript file automatically loads project data from a JSON file
 * and creates interactive project cards on the Projects page.
 * 
 * HOW IT WORKS:
 * 1. When the page loads, fetch /assets/data/projects.json
 * 2. For each project in the JSON, create a clickable card with image and text
 * 3. If loading fails, show a helpful error message
 * 
 * The JSON file is generated by running: npm run build:projects
 */

// Wait for the HTML page to fully load before running our code
document.addEventListener('DOMContentLoaded', function() {
  // Find the container where we'll add the project cards
  const projectsGrid = document.getElementById('projects-grid');

  // If we can't find the grid container, stop here
  if (!projectsGrid) {
    console.error('Projects grid container not found');
    return;
  }

  /**
   * FUNCTION: Create a single project card element
   * Takes project data and returns a complete HTML element
   */
  function createProjectCard(project) {
    // Create the main card link (the entire card is clickable)
    const cardLink = document.createElement('a');
    cardLink.className = 'project-card'; // CSS class for styling
    cardLink.href = project.href; // Where the link goes when clicked
    cardLink.setAttribute('aria-label', `Open ${project.title}`); // Screen reader description

    // Create the image container
    const figure = document.createElement('figure');
    figure.className = 'project-thumb';

    // Create the actual image
    const img = document.createElement('img');
    img.src = project.thumbnail; // Image file path
    img.alt = `Thumbnail for ${project.title}`; // Screen reader description
    img.loading = 'lazy'; // Only load image when it's about to be visible (saves bandwidth)
    img.decoding = 'async'; // Improves page performance

    // Add the image to the figure container
    figure.appendChild(img);

    // Create the text information container
    const metaDiv = document.createElement('div');
    metaDiv.className = 'project-meta';

    // Create the project title
    const title = document.createElement('h3');
    title.className = 'project-title';
    title.textContent = project.title; // The project name

    // Create the project description
    const description = document.createElement('p');
    description.className = 'project-desc';
    description.textContent = project.description; // The project description

    // Add title and description to the meta container
    metaDiv.appendChild(title);
    metaDiv.appendChild(description);

    // Add both the image and text to the main card
    cardLink.appendChild(figure);
    cardLink.appendChild(metaDiv);

    return cardLink;
  }

  /**
   * FUNCTION: Display an error message when projects can't be loaded
   */
  function showErrorMessage() {
    // Create a helpful error message container
    const errorDiv = document.createElement('div');
    errorDiv.className = 'projects-error';
    errorDiv.style.textAlign = 'center';
    errorDiv.style.padding = '2rem';
    errorDiv.style.color = 'var(--leaf-dark)';

    // Create the error message content
    errorDiv.innerHTML = `
      <h3 style="margin-bottom: 1rem; color: var(--berry);">No Projects Found</h3>
      <p style="margin-bottom: 1rem;">The projects data couldn't be loaded.</p>
      <p style="font-size: 0.9rem; opacity: 0.8;">
        To fix this, run the build script: <code style="background: var(--sand); padding: 0.2rem 0.5rem; border-radius: 4px;">npm run build:projects</code>
      </p>
    `;

    // Add the error message to the grid
    projectsGrid.appendChild(errorDiv);
  }

  /**
   * FUNCTION: Load and display all projects
   */
  async function loadProjects() {
    try {
      // Show a loading message while we fetch the data
      projectsGrid.innerHTML = '<p style="text-align: center; color: var(--leaf-dark); opacity: 0.7;">Loading projects...</p>';

      // Fetch the JSON data from the server
      // fetch() is a modern way to request data from a URL
      const response = await fetch('/assets/data/projects.json');

      // Check if the request was successful
      if (!response.ok) {
        throw new Error(`Failed to load projects: ${response.status} ${response.statusText}`);
      }

      // Convert the response to JavaScript object format
      const projects = await response.json();

      // Clear the loading message
      projectsGrid.innerHTML = '';

      // Check if we actually have any projects
      if (!projects || projects.length === 0) {
        showErrorMessage();
        return;
      }

      // Create a card for each project and add it to the grid
      projects.forEach(project => {
        const card = createProjectCard(project);
        projectsGrid.appendChild(card);
      });

      console.log(`âœ… Loaded ${projects.length} projects successfully`);

    } catch (error) {
      // If anything goes wrong, show an error message
      console.error('Error loading projects:', error);
      projectsGrid.innerHTML = '';
      showErrorMessage();
    }
  }

  // Start loading the projects when the script runs
  loadProjects();
});

